Scripts.OnEnter:
local name = LI.AddCursorItem()
if name then this:SetText(name) this:ClearFocus()
LI.Search(this) else LI.FilterExplain(this) end

Scripts.OnReceiveDrag:
local name = LI.AddCursorItem()
if name then this:SetText(name) this:ClearFocus()
LI.Search(this) end

Scripts.OnClick:
local name = LI.AddCursorItem()
if name then this:SetText(name) this:ClearFocus()
LI.Search(this) end

Scripts.OnMouseUp:
this:StopMovingOrSizing()
if LootIt_FilterHelp_Itemname.link then LootIt_GameTooltip2:SetHyperLink(LootIt_FilterHelp_Itemname.link)
if yaCIt then yaCIt.ShowTooltipBySelf(LootIt_GameTooltip2) end end
